import React, { useState } from "react";

const Header = () => {
  const [visible, setVisible] = useState(false);

  const toggleVisible = () => {
    const scrolled = document.documentElement.scrollTop;
    if (scrolled > 50) {
      setVisible(true);
    } else if (scrolled <= 50) {
      setVisible(false);
    }
  };

  return (
    <header>
      <div className={visible?"header-act":"header-dact"}>
        
      </div>
    </header>
  );
};

export default Header;
